mapper.update:
update employee
	 set emp_join = #{param1}, emp_position = #{param2}, emp_depart = #{param3}
 where emp_id = #{param4}

mapper.select:
select a.emp_name, a.emp_gender, b.dep_name, c.position_name, c.job_type, a.emp_mobile, a.emp_email, a.emp_id, a.emp_img from employee a, department b, position c where a.emp_depart = b.dep_id and a.emp_position = c.position_id
AND a.emp_name like '%' || #{param1} ||'%'
AND a.emp_mobile like '%' ||#{param2} ||'%'
AND a.emp_email like '%' ||#{param3} ||'%'
AND b.dep_name = #{param4}
AND c.position_name = #{param5}
